interface Props{
  className?: string,
  direction?: string,
  iconLeft?: boolean,
  iconRight?: boolean,
  multiItem?: boolean,
  onClick?: any,
  tag?: any,
  testimonial?: boolean,
}

const Control = (props: Props) => {
  const {
    className,
    direction,
    iconLeft,
    iconRight,
    multiItem,
    onClick,
    tag: Tag,
    testimonial,
  } = props;

  let text;

  if (direction === "prev") {
    text = "Previous";
  } else if (direction === "next") {
    text = "Next";
  }

  let classes = classNames(`carousel-control-${direction}`, className);

  let caretClasses = classNames(`carousel-control-${direction}-icon`);

  if (testimonial) {
    const arrow = direction === "prev" ? "left" : "right";
    classes = classNames(
      `carousel-control-${direction}`,
      arrow,
      "carousel-control",
      className
    );
    caretClasses = classNames(`icon-${direction}`);
  }

  if (multiItem) {
    classes = classNames("btn-floating");
  }

  return (
    <ThemeProvider theme={theme}>
      <Tag
        data-test="carousel-control"
        className={classes}
        data-slide={direction}
        onClick={onClick}
      >
        {iconLeft ? (
          <i className="fab fa-chevron-left"></i>
