          <i className="fab fa-chevron-right"></i>
        ) : (
          <div>
            <span className={caretClasses} aria-hidden="true" />
            <span className="sr-only">{text}</span>
          </div>
        )}
      </Tag>
    </ThemeProvider>
  );
};

