    sorted,
    striped,
    tbodyColor,
    tbodyTextWhite,
    theadColor,
    theadTextWhite,
    translateScrollHead,
    ...attributes
  } = props

  const minWidth = scrollX
    ? `${columns
      .map((col) => col.width)
      .reduce((prev, curr) => prev + curr, 0)}px`
    : 'auto'

  return (
    <ThemeProvider theme={theme}>
      <div data-test='datatable-table-scroll' className='col-sm-12'>
        <div className='dataTables_scroll'>
