                <DataTableHead
                  color={theadColor}
                  textWhite={theadTextWhite}
                  columns={columns}
                  handleSort={handleSort}
                  scrollX={scrollX}
                  scrollY={scrollY}
                  sortable={sortable}
                  sorted={sorted}
                />
              </Table>
            </div>
          </div>

          <div
            className='dataTable_scrollBody'
            style={{ overflow: 'auto' }}
            onScroll={handleTableBodyScroll}
          >
            <Table
              theadColor={''} wrapperClassName={''}
              // style={{
              //   minWidth
              // }}
              autoWidth={autoWidth}
              bordered={bordered}
              borderless={borderless}
