              maxHeight={maxHeight}
              responsive={responsive}
              responsiveSm={responsiveSm}
              responsiveMd={responsiveMd}
              responsiveLg={responsiveLg}
              responsiveXl={responsiveXl}
              scrollY={scrollY}
              small={small}
              striped={striped}
              className='dataTable'
              {...attributes}            >
              <colgroup>
                {columns.map((col) => (
                  <col
                    key={col.field}
                    style={{
                      width: `${col.width}px` || 'auto',
                      minWidth: `${col.width}px` || 'auto'
                    }}
                  />
                ))}
              </colgroup>
              <TableBody
                color={tbodyColor}
                textWhite={tbodyTextWhite}
                rows={rows}
                columns={columns} children={undefined} />
              {children}
            </Table>
          </div>
        </div>
      </div>
    </ThemeProvider>
  )
}

