import { Component } from "./PageLink.style";
import { ThemeProvider } from "styled-components";
import { theme } from "../../../theme";

interface Props {
  children?: React.ReactNode,
  disabled?: boolean,
  className?: string,
  tag?: Function | string,
  onClick?: React.MouseEventHandler<HTMLAnchorElement>
}

const PageLink = (props: Props) => {
  const { children, className, tag, disabled, ...attributes } = props;

  const pageLinkClasses = classNames({ disabled }, "page-link", className);

  let pageLinkComponent = (
    <ThemeProvider theme={theme}>
      <Component
        as={(tag as unknown) as undefined}
        data-test="page-link"
        {...attributes}
        className={pageLinkClasses}
      >
        {children}
      </Component>
    </ThemeProvider>
  );

  return pageLinkComponent;
};

PageLink.propTypes = {
  children: PropTypes.node,
  disabled: PropTypes.bool,
  className: PropTypes.string,
  tag: PropTypes.oneOfType([PropTypes.func, PropTypes.string]),
};

PageLink.defaultProps = {
  tag: "a",
  disabled: false,
};

export default PageLink;
export { PageLink as CDBPageLink };
import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import Pagination from '../../Pagination';
import PageItem from '../../Pagination/PageItem';
import PageLink from '../../Pagination/PageLink';
import { ThemeProvider } from "styled-components";
import { theme } from "../../../theme";



interface Props {
  activePage?: number,
  changeActivePage?: Function,
  label?: string[],
  pages?: any,
  pagesAmount?: number
  className?: string,
  children?: React.ReactNode,
}

const DataTablePagination = (props: Props) => {


  const { activePage, changeActivePage, className, pages, label, ...attributes } = props;
  const [state, setState] = useState<{
    pages: any,
    pGroups: any[]
  }>({
    pages: pages,
    pGroups: []
  })
  const [pGroupByPNum, setPGroupByPNum] = useState([])

  // ComponentDidMount & ComponentDidUpdateLogic
  const mounted = useRef<boolean>()
  useEffect(() => {

    if (!mounted.current) { // Component Did Mount
      groupPages();
      mounted.current = true

    } else { // Compoennt Did Update
      setState((prev) => ({ ...prev, pages }))
    }

  }, [pages])

  useEffect(() => {
    if (!state.pages) return

    groupPages()
  }, [state.pages])

  // useEffect(() => {

  //   const prevProps = () => {
  //     const { pages } = props;

  //     if (prevProps.pages !== pages) {
  //       setState({ pages: pages },
  //         groupPages());
  //     }
  //   }
  // });

  const pagesIndexify = () => {
    const { pages } = state;

    const mutablePages = [...pages];
    mutablePages.forEach((page, index) => (page.index = index));
    return mutablePages;
  };

  const groupPages = () => {
    const pGroups: any[] = [];
    const pages = pagesIndexify();
    const { pagesAmount } = props;

    while (pages.length > 0) {
      pGroups.push(pages.splice(0, pagesAmount));
    }

    setState((prev) => ({ ...prev, pGroups }));
  };

  const choosePagesGroup = () => {
    const { activePage, pagesAmount } = props;
    const { pGroups } = state;
    const pGroupNumber = Math.floor(activePage / pagesAmount);

    setPGroupByPNum(pGroups.length ? pGroups[pGroupNumber] : [])
  };

  useEffect(() => {
    const { pGroups } = state;
    if (pGroups && props.pagesAmount) {
      choosePagesGroup()
    }
  }, [state.pGroups, props.activePage, props.pagesAmount])

  return (
    <ThemeProvider theme={theme}>
      <div data-test='datatable-pagination' className='col-sm-12 col-md-7 d-flex justify-content-end'>
        <div className='dataTables_paginate'>
          <Pagination sm>
            <PageLink
              disabled={activePage <= 0}
              aria-label={label[0]}
              onClick={() => changeActivePage(activePage - 1)}
              className=""
            >
              <span>{label[0]}</span>
            </PageLink>

            {pGroupByPNum.length > 0 && pGroupByPNum.map((page, index) => {
              return (
              <PageItem
                key={Object.keys(page[0])[0] + page.index}
                active={page.index === activePage}
                onClick={() => changeActivePage(page.index)}
              >
                {page.index + 1}
                {page.index === activePage && (
                  <span className='sr-only'>(current)</span>
                )}
              </PageItem>
            )})}

            <PageLink
              disabled={!pages.length || activePage === pages.length - 1}
              aria-label={label[1]}
              onClick={() => changeActivePage(activePage + 1)}
              className=""
            >
              <span>{label[1]}</span>
            </PageLink>
          </Pagination>
        </div>
      </div>
    </ThemeProvider>
  );

}

DataTablePagination.propTypes = {
  activePage: PropTypes.number.isRequired,
  changeActivePage: PropTypes.func.isRequired,
  label: PropTypes.arrayOf(PropTypes.string).isRequired,
  pages: PropTypes.array.isRequired,
  pagesAmount: PropTypes.number.isRequired,
  className : PropTypes.string,
  children: PropTypes.node
};

DataTablePagination.defaultProps = {
  children: "",
  className: ""
}

export default DataTablePagination;
export { DataTablePagination as MDBDataTablePagination };
import React, { useEffect, useState, useRef } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'
import DataTableTable from './DataTableTable'
import DataTableTableScroll from './DataTableTableScroll'
import DataTableEntries from './DataTableEntries'
import DataTableSearch from './DataTableSearch'
import DataTableInfo from './DataTableInfo'
import DataTablePagination from './DataTablePagination'
import { ThemeProvider } from 'styled-components'
import { theme } from '../../theme'

interface Props {
  autoWidth?: boolean
  barReverse?: boolean
  bordered?: boolean
  borderless?: boolean
  btn?: boolean
  children?: React.ReactNode
  className?: string
  dark?: boolean
  data?:
  | string
  | {
    columns: any[]
    rows: any[]
  }
  disableRetreatAfterSorting?: boolean
  displayEntries?: boolean
  entries?: number
  entriesLabel?: number | object | string
  entriesOptions?: number[]
  exportToCSV?: boolean
  filter?: string
  fixed?: boolean
  hover?: boolean
  info?: boolean
  infoLabel?: string[]
  materialSearch?: boolean
  maxHeight?: string
  noBottomColumns?: boolean
  noRecordsFoundLabel?: string
  onPageChange?: Function
  onSearch?: Function
  onSort?: Function
  order?: string[]
  pagesAmount?: number
  paginationLabel?: string[]
  paging?: boolean
  proSelect?: boolean
  responsive?: boolean
  responsiveLg?: boolean
  responsiveMd?: boolean
  responsiveSm?: boolean
  responsiveXl?: boolean
  scrollX?: boolean
  scrollY?: boolean
  searching?: boolean
  searchLabel?: string
  small?: boolean
  sortable?: boolean
  sortRows?: string
  striped?: boolean
  tbodyColor?: string
  tbodyTextWhite?: boolean
  theadColor?: string
  theadTextWhite?: boolean
  rows?: any
}

const DataTable = (props: Props) => {
  const {
    autoWidth,
    barReverse,
    bordered,
    borderless,
    btn,
    children,
    className,
    dark,
    data,
    disableRetreatAfterSorting,
    displayEntries,
    entriesLabel,
    entriesOptions,
    exportToCSV,
    filter,
    fixed,
    hover,
    info,
    infoLabel,
    maxHeight,
    noBottomColumns,
    noRecordsFoundLabel,
    onPageChange,
    onSearch,
    onSort,
    order,
    pagesAmount,
    paginationLabel,
    paging,
    responsive,
    responsiveLg,
    responsiveMd,
    responsiveSm,
    responsiveXl,
    scrollX,
    scrollY,
    searching,
    searchLabel,
    small,
    sortable,
    rows,
    sortRows,
    striped,
    tbodyColor,
    tbodyTextWhite,
    theadColor,
    materialSearch,
    theadTextWhite,
    proSelect,
    ...attributes
  } = props

  type DataTableStateType = {
    activePage: number
    columns: any[]
    entries: number
    filteredRows: any[]
    filterOptions: any[]
    order: string[]
    pages: any[]
    rows: any[]
    search: string
    searchSelect: string
    sorted: boolean
    translateScrollHead: number
    unsearchable: any[]
  }
  const [state, setState] = useState<DataTableStateType>({
    activePage: 0,
    columns: (typeof props.data === 'object' && props.data.columns) || [],
    entries: props.entries,
    filteredRows: (typeof props.data === 'object' && props.data.rows) || [],
    filterOptions: [],
    order: props.order || [],
    pages: [],
    rows: (typeof props.data === 'object' && props.data.rows) || [],
    search: '',
    searchSelect: '',
    sorted: false,
    translateScrollHead: 0,
    unsearchable: []
  })

  //Component did mount and component did update
  const mounted = useRef<boolean>()
  useEffect(() => {
    if (!mounted.current) {
      // component did mount
      componentDidMountCallback()
      mounted.current = true
    } else {
      // component did update
      componentDidUpdateCallback()
    }
  }, [data])

  useEffect(() => filterRows(), [state.rows, state.columns, state.sorted])

  const componentDidMountCallback = function () {
    const { data, paging } = props
    const { order, columns, pages, rows } = state

    if (typeof data === 'string') {
      fetchData(data, true)
    }

    if (order.length > 0) {
      handleSort(order[0], order[1])
    } else {
      handleSort()
    }

    setUnsearchable(columns)

    if (paging) {
      paginateRowsInitialy()
    } else {
      pages.push(rows)
    }
  }

  const componentDidUpdateCallback = function () {
    const { columns } = state
    const { data } = props
    typeof data === 'string'
      ? fetchData(data, true)
      : setData(data.rows, data.columns, paginateRows)

    setUnsearchable(columns)
    filterRows()
  }

  const setData = (
    rows: any[] = [],
    columns: any[] = [],
    callback?: () => void
  ) => {
    setState({
      ...state,
      columns,
      rows,
      filteredRows: rows
      //   filteredRows: props.disableRetreatAfterSorting ? filterRows() : rows
  })

   props.disableRetreatAfterSorting && filterRows()
    callback && typeof callback === 'function' && callback()
  }

  const setUnsearchable = function (columns) {
    const unsearchable: any[] = []

    columns.forEach((column) => {
      if (column.searchable !== undefined && column.searchable === false) {
        unsearchable.push(column.field)
      }
    })

    setState((prevState) => ({ ...prevState, unsearchable }))
  }

  const fetchData = function (link: string, isPaginateRows: boolean) {
    fetch(link)
      .then((res) => res.json())
      .then((json) =>
        setData(
          json.rows,
          json.columns,
          isPaginateRows ? paginateRows : undefined
        )
      )
      .catch((err) => console.log(err))
  }

  const getPagesAmount = () =>
    Math.ceil(state.filteredRows.length / state.entries)

  const paginateRowsInitialy = () => {
    const { rows, entries, pages } = state

    const pagesAmount = getPagesAmount()

    for (let i = 1; i <= pagesAmount; i++) {
      const pageEndIndex = i * entries
      pages.push(rows.slice(pageEndIndex - entries, pageEndIndex))
    }
  }

  const handleEntriesChange = (value) => {
    setState((prevState) => ({
      ...prevState,
      entries: Array.isArray(value) ? value[0] : value
    }))    
  }

  // Runs after entry changes
  useEffect(() => {
    if (!state.entries) return

    paginateRows()
  }, [state.entries])

  const handleSearchChange = (e) => {
    setState((prevState) => ({ ...prevState, search: e.target.value }))
    props.onSearch &&
        typeof props.onSearch === 'function' &&
        props.onSearch(e.target.value)
  }

  useEffect(() => {
    filterRows()
  }, [state.search])

  const checkFieldValue = (array, field) => {
    return array[field] && typeof array[field] !== 'string'
      ? array[field].props.searchvalue
      : array[field]
  }

  const checkField = (field, a, b, direction) => {
    const [aField, bField] = [
      checkFieldValue(a, field),
      checkFieldValue(b, field)
    ]

    let comp = aField > bField ? -1 : 1
    if (direction === 'asc') {
      comp *= -1
    }

    return comp
  }

  const sort = (rows, sortRows, field, direction) => {
    rows.sort((a, b) => {
      if (sortRows && sortRows.includes(field)) {
        return checkField(field, a, b, direction)
      }

      return direction === 'asc'
        ? a[field] < b[field]
          ? -1
          : 1
        : a[field] > b[field]
          ? -1
          : 1
    })
  }

  const handleSort = (field?, sortType?) => {
    const { onSort } = props

    if (sortType === 'disabled') {
      return
    }

    setState((prevState) => {
      const { sortRows } = props
      const { rows, columns } = prevState
      const direction = sortType === 'desc' ? 'desc' : 'asc'

      sort(rows, sortRows, field, direction)

      columns.forEach((col) => {
        if (col.sort === 'disabled') {
          return
        }

        col.sort =
          col.field === field ? (col.sort === 'desc' ? 'asc' : 'desc') : ''
      })

      return {
        ...prevState,
        rows,
        columns,
        sorted: true
      }
    })

    onSort &&
      typeof onSort === 'function' &&
      onSort({ column: field, direction: sortType === 'desc' ? 'desc' : 'asc' })
  }

  const filterRows = (search = state.search) => {
    const { unsearchable } = state
    const { sortRows, noRecordsFoundLabel } = props

    setState((prevState) => {
      const filteredRows = prevState.rows.filter((row) => {
        for (const key in row) {
          if (
            (!unsearchable.length || !unsearchable.includes(key)) &&
            typeof row[key] !== 'function'
          ) {
            let stringValue = ''

            if (sortRows && typeof row[key] !== 'string') {
              const content: any[] = []
              const getContent = (element) =>
                typeof element === 'object'
                  ? element.props.children &&
                  Array.from(element.props.children).map((el) =>
                    getContent(el)
                  )
                  : content.push(element)

              getContent(row[key])
              stringValue = content.join('')
            } else if (row[key]) {
              stringValue = row[key].toString()
            }
            if (stringValue.toLowerCase().includes(search.toLowerCase())) {
              return true
            }
          }
        }
        return false
    })

      if (filteredRows.length === 0) {
        filteredRows.push({
          message: noRecordsFoundLabel,
          colspan: prevState?.columns?.length
        })
      }
      let test = { ...state}
      if (props.disableRetreatAfterSorting) {
        test = {
          ...state,
          filteredRows,
          activePage: prevState.activePage =
            prevState.activePage < prevState.pages.length ||
              prevState.activePage === 0
              ? prevState.activePage
              : prevState.pages.length - 1
        }
      } else if (!props.disableRetreatAfterSorting) {
        test = { ...state, filteredRows, activePage: 0 }
      }

      return test
    })
  }

  useEffect(() => {
    paginateRows()
  }, [state.filteredRows, state.activePage])

  const paginateRows = () => {
    const pagesAmount = getPagesAmount()

    setState((prevState) => {
      let { pages, entries, filteredRows, activePage } = prevState
      const { paging, disableRetreatAfterSorting } = props

      pages = []

      if (paging) {
        for (let i = 1; i <= pagesAmount; i++) {
          const pageEndIndex = i * entries
          pages.push(filteredRows.slice(pageEndIndex - entries, pageEndIndex))
        }
        if (!disableRetreatAfterSorting) {
          activePage =
            activePage < pages.length || activePage === 0
              ? activePage
              : pages.length - 1
        }
      } else {
        pages.push(filteredRows)
        activePage = 0
      }

      return { ...prevState, pages, filteredRows, activePage }
    })
  }

  const changeActivePage = (page) => {
    const { onPageChange } = props
    setState({ ...state, activePage: page })

    onPageChange &&
      typeof onPageChange === 'function' &&
      onPageChange({ activePage: page + 1, pagesAmount: getPagesAmount() })
  }

  const handleTableBodyScroll = (e) => {
    setState({ ...state, translateScrollHead: e.target.scrollLeft })
  }

  const {
    columns,
    entries,
    filteredRows,
    pages,
    activePage,
    search,
    sorted,
    translateScrollHead
  } = state

  const tableClasses = classNames('dataTables_wrapper', className)

  return (
    <ThemeProvider theme={theme}>
      <div data-test='datatable' className={tableClasses}>
        <div className="d-flex flex-wrap justify-content-between px-2">
          {barReverse ? (
            <React.Fragment>
              <DataTableSearch
                handleSearchChange={handleSearchChange}
                search={search}
                searching={searching}
                label={searchLabel}
                barReverse={barReverse}
                materialSearch={materialSearch}
              />
              <DataTableEntries
                paging={paging}
                displayEntries={displayEntries}
                entries={entries}
                handleEntriesChange={handleEntriesChange}
                entriesArr={entriesOptions}
                label={entriesLabel}
                barReverse={barReverse}
                proSelect={proSelect}
              />
            </React.Fragment>
          ) : (
            <React.Fragment>
              <DataTableEntries
                paging={paging}
                displayEntries={displayEntries}
                entries={entries}
                handleEntriesChange={handleEntriesChange}
                entriesArr={entriesOptions}
                label={entriesLabel}
                barReverse={barReverse}
                proSelect={proSelect}
              />
              <DataTableSearch
                handleSearchChange={handleSearchChange}
                search={search}
                searching={searching}
                label={searchLabel}
                barReverse={barReverse}
                materialSearch={materialSearch}
              />
            </React.Fragment>
          )}
        </div>
        {!scrollY && !scrollX && (
          <div className='row'>
            <DataTableTable
              autoWidth={autoWidth}
              bordered={bordered}
              borderless={borderless}
              btn={btn}
              dark={dark}
              fixed={fixed}
              hover={hover}
              noBottomColumns={noBottomColumns}
              noRecordsFoundLabel={noRecordsFoundLabel}
              responsive={responsive}
              responsiveSm={responsiveSm}
              responsiveMd={responsiveMd}
              responsiveLg={responsiveLg}
              responsiveXl={responsiveXl}
              small={small}
              striped={striped}
              theadColor={theadColor}
              theadTextWhite={theadTextWhite}
              columns={columns}
              handleSort={handleSort}
              sortable={sortable}
              tbodyColor={tbodyColor}
              tbodyTextWhite={tbodyTextWhite}
              rows={pages[activePage]}
              sorted={sorted}
              {...attributes}
            />
          </div>
        )}
        {(scrollY || scrollX) && (
          <div className='row'>
            <DataTableTableScroll
              autoWidth={autoWidth}
              bordered={bordered}
              borderless={borderless}
              btn={btn}
              dark={dark}
              fixed={fixed}
              handleTableBodyScroll={handleTableBodyScroll}
              hover={hover}
              maxHeight={maxHeight}
              responsive={responsive}
              responsiveSm={responsiveSm}
              responsiveMd={responsiveMd}
              responsiveLg={responsiveLg}
              responsiveXl={responsiveXl}
              scrollX={scrollX}
              scrollY={scrollY}
              small={small}
              striped={striped}
              theadColor={theadColor}
              theadTextWhite={theadTextWhite}
              columns={columns}
              handleSort={handleSort}
              sortable={sortable}
              sorted={sorted}
              tbodyColor={tbodyColor}
              tbodyTextWhite={tbodyTextWhite}
              rows={pages[activePage]}
              translateScrollHead={translateScrollHead}
              {...attributes}
            />
          </div>
        )}
        {paging && (
          <div className="d-flex flex-wrap justify-content-between px-2">
            <DataTableInfo
              activePage={activePage}
              entries={entries}
              filteredRows={filteredRows}
              info={info}
              pages={pages}
              label={infoLabel}
              noRecordsFoundLabel={noRecordsFoundLabel}
            />
            <DataTablePagination
              activePage={activePage}
              changeActivePage={changeActivePage}
              pages={pages}
              pagesAmount={pagesAmount}
              label={paginationLabel}
            />
          </div>
        )}
      </div>
    </ThemeProvider>
  )
}

DataTable.propTypes = {
