const Null = () => null

const modifiers = [
  {
    name: 'flip',
    enabled: false
  },
  {
    name: 'hide',
    enabled: false
  }
]

const popperModifiers = [
  ...modifiers,
  {
    name: 'arrow',
    options: {
      padding: 5
    }
  },
  {
    name: 'offset',
    options: {
      offset: [0, 14]
    }
  }
]

const animatedModifiers = [
  ...popperModifiers,
  {
    name: 'computeStyles',
    options: {
      adaptive: false
    }
  },

  {
    name: 'computeStyles',
    options: {
