const DropDownMenu = (props: Props) => {
  const {
    className,
    tag,
    color,
    children,
    right,
    dropleft,
    dropright,
    top,
    dropdown,
    dropup,
    ...attrs
  } = props
  const { isOpen } = useContext<any>(DropDownContext)
  const [isOpenValue] = isOpen

  useEffect(() => {
    const { dropup, dropleft, dropright } = props
    const position1 = dropup
      ? 'top'
      : dropright
      ? 'right'
      : dropleft
      ? 'left'
      : 'bottom'

    const position2 = right ? 'end' : 'start'

    attrs.placement = `${position1}-${position2}`
  })

  const dropDownMenuClasses = classNames('dropdown-menu', className)

  let dropDownMenuComponent = (
    <ThemeProvider theme={theme}>
      <Transition
        items={isOpenValue}
        from={{ opacity: 0, scale: 0.8, top: 0 }}
        enter={{ opacity: 1, scale: 1, top: 0 }}
        leave={{ opacity: 0, scale: 0.8, top: 0 }}
        config={{ mass: 1, tension: 500, friction: 0, duration: 200 }}
      >
        {(show) =>
          show
            ? ({ scale, opacity, top: topOffset }) => (
                <Popper
                  placement={attrs.placement}
                  modifiers={animatedModifiers}
                >
                  {({
                    ref,
                    style: { top, left, position },
                    placement,
                    arrowProps
                  }) => {
                    const newTop = typeof top === 'string' && top ? parseInt(top) : top
                    const topVal = (!isNaN(+newTop) ? +newTop : 0) + topOffset
                    return (
                      <Component
                        className={dropDownMenuClasses}
                        ref={ref}
                        style={{
                          opacity,
                          top: 0,
                          left: 0,
                          position,
                          padding: '1em',
                          transform: `translate3d(${left}, ${topVal}px, 0) scale(${scale})`,
                          transformOrigin: 'top center'
                        }}
                        data-placement={placement}
                      >
                        {children}
                      </Component>
                    )
                  }}
                </Popper>
              )
            : Null
        }
      </Transition>
    </ThemeProvider>
  )

  return dropDownMenuComponent
}

DropDownMenu.defaultProps = {
