export const returnAttributes = (attributes: any) => {
  const newAttributesObject = Object.keys(attributes).reduce(
    (previousValue: any, currentElement) => {
      if (attributes[currentElement]) {
        previousValue[currentElement] = attributes[currentElement];
      }
      return previousValue;
    },
    {}
  );

  return newAttributesObject;
};

export const getColorClass = (color : string) => {
  const colorArray = color.split(" ");
  const specialColors = [
    "danger",
    "warning",
    "success",
    "info",
    "default",
    "primary",
    "secondary",
    "elegant",
    "stylish",
    "unique",
    "special",
  ];
  let colorClasses = "";

  colorArray.forEach((color) => {
    if (specialColors.includes(color)) {
      if (color.includes("dark")) {
        color.replace("-", "-color-");
        colorClasses += `${color} `;
      } else {
        colorClasses += `${color}-color`;
      }
    } else {
      colorClasses += `${color} `;
    }
  });

  return colorClasses;
};

export function debounce(fn: any, time = 166) {
  let timeout: NodeJS.Timer;

  function debounced(this: any, ...args: any) {
    const that = this;
    const later = () => {
      fn.apply(that, args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, time);
  }

  debounced.clear = () => {
    clearTimeout(timeout);
  };

  return debounced;
}

export const makeFirstLetterUpper = (element: string) => {
  return element.charAt(0).toUpperCase() + element.slice(1);
